import React, { useState, useEffect, useRef } from 'react';
import './App.css';

function App() {
  const [servers, setServers] = useState([]);
  const [currentServer, setCurrentServer] = useState(null);
  const [reports, setReports] = useState([]);
  const [clues, setClues] = useState([]);
  const [ipTracking, setIpTracking] = useState([]);
  const [events, setEvents] = useState([]);
  const [serverIp, setServerIp] = useState('');
  const [serverPort, setServerPort] = useState('');
  const [isWsConnected, setIsWsConnected] = useState(false);
  const [isTcpConnected, setIsTcpConnected] = useState(false);

  const tcpSocket = useRef(null);
  const udpSocket = useRef(null);
  const wsSocket = useRef(null);

  // Подключение к серверу
  const connectToServer = (server) => {
    disconnectFromServer();

    setCurrentServer(server);

    // TCP соединение для отчетов
    tcpSocket.current = new WebSocket(`ws://${server.ip}:${server.tcpPort}`);
    tcpSocket.current.onopen = () => {
      setIsTcpConnected(true);
      console.log('TCP connection established');
    };
    tcpSocket.current.onmessage = (e) => {
      setReports(prev => [...prev, {
        type: 'report',
        content: e.data,
        timestamp: new Date().toISOString()
      }].slice(-50));
    };
    tcpSocket.current.onclose = () => {
      setIsTcpConnected(false);
      console.log('TCP connection closed');
    };
    tcpSocket.current.onerror = (error) => {
      console.error('TCP error:', error);
    };

    // WebSocket соединение для улик
    wsSocket.current = new WebSocket(`ws://${server.ip}:${server.wsPort}`);
    wsSocket.current.onopen = () => {
      setIsWsConnected(true);
      console.log('WebSocket connection established');
    };
    wsSocket.current.onmessage = (e) => {
      const data = JSON.parse(e.data);
      if (data.type === 'new_clue') {
        setClues(prev => [...prev, data].slice(-20));
      } else if (data.type === 'daily_event') {
        setEvents(prev => [...prev, data].slice(-10));
        alert(`Событие дня: ${data.event}`);
      }
    };
    wsSocket.current.onclose = () => {
      setIsWsConnected(false);
      console.log('WebSocket connection closed');
    };
    wsSocket.current.onerror = (error) => {
      console.error('WebSocket error:', error);
    };

    // UDP "соединение" для отслеживания IP
    udpSocket.current = new WebSocket(`ws://${server.ip}:${server.udpPort}`);
    udpSocket.current.onmessage = (e) => {
      setIpTracking(prev => [...prev, {
        content: e.data,
        timestamp: new Date().toISOString()
      }].slice(-30));
    };
  };

  // Отключение от сервера
  const disconnectFromServer = () => {
    if (tcpSocket.current) {
      tcpSocket.current.close();
      setIsTcpConnected(false);
    }
    if (udpSocket.current) udpSocket.current.close();
    if (wsSocket.current) {
      wsSocket.current.close();
      setIsWsConnected(false);
    }
    setCurrentServer(null);
  };

  // Добавление нового сервера
  const addServer = () => {
    if (serverIp && serverPort) {
      const newServer = {
        id: Date.now(),
        ip: serverIp,
        tcpPort: parseInt(serverPort) + 0,
        udpPort: parseInt(serverPort) + 1,
        wsPort: parseInt(serverPort) + 2
      };
      setServers(prev => [...prev, newServer]);
      setServerIp('');
      setServerPort('');
    }
  };

  // Удаление сервера
  const removeServer = (id) => {
    setServers(prev => prev.filter(s => s.id !== id));
    if (currentServer && currentServer.id === id) {
      disconnectFromServer();
    }
  };

  // Отправка отчета на сервер
  const sendReport = () => {
    if (!isTcpConnected || !tcpSocket.current) {
      alert('TCP соединение не установлено!');
      return;
    }

    const reportText = prompt('Введите текст отчета:');
    if (reportText) {
      try {
        tcpSocket.current.send(reportText);
      } catch (error) {
        console.error('Ошибка при отправке отчета:', error);
        alert('Ошибка при отправке отчета. Проверьте соединение.');
      }
    }
  };

  return (
    <div className="app">
      <h1>Кибер-Сыщик</h1>
      <div className="server-management">
        <h2>Управление серверами</h2>
        <div>
          <input 
            type="text" 
            placeholder="IP сервера" 
            value={serverIp}
            onChange={(e) => setServerIp(e.target.value)}
          />
          <input 
            type="number" 
            placeholder="Базовый порт" 
            value={serverPort}
            onChange={(e) => setServerPort(e.target.value)}
          />
          <button onClick={addServer}>Добавить сервер</button>
        </div>

        <div className="server-list">
          <h3>Список серверов:</h3>
          <ul>
            {servers.map(server => (
              <li key={server.id}>
                {server.ip}:{server.tcpPort}/{server.udpPort}/{server.wsPort}
                <button onClick={() => connectToServer(server)}>
                  {currentServer?.id === server.id ? 'Подключен' : 'Подключиться'}
                </button>
                <button onClick={() => removeServer(server.id)}>Удалить</button>
              </li>
            ))}
          </ul>
        </div>
      </div>

      {currentServer && (
        <div className="investigation-panel">
          <div className="panel-section">
            <h2>Отчеты (TCP) {isTcpConnected ? '✅' : '❌'}</h2>
            <button onClick={sendReport} disabled={!isTcpConnected}>
              Отправить отчет
            </button>
            <ul>
              {reports.map((report, i) => (
                <li key={i}>{report.timestamp}: {report.content}</li>
              ))}
            </ul>
          </div>

          <div className="panel-section">
            <h2>Новые улики (WebSocket) {isWsConnected ? '✅' : '❌'}</h2>
            <ul>
              {clues.map((clue, i) => (
                <li key={i}>{clue.timestamp}: {clue.clue}</li>
              ))}
            </ul>
          </div>

          <div className="panel-section">
            <h2>Отслеживание IP (UDP)</h2>
            <ul>
              {ipTracking.map((ip, i) => (
                <li key={i}>{ip.timestamp}: {ip.content}</li>
              ))}
            </ul>
          </div>

          <div className="panel-section">
            <h2>События дня</h2>
            <ul>
              {events.map((event, i) => (
                <li key={i}>{event.timestamp}: {event.event}</li>
              ))}
            </ul>
          </div>
        </div>
      )}
    </div>
  );
}

export default App;
